CREATE TABLE mytable (
  no TEXT PRIMARY KEY,
  brandName TEXT COLLATE NOCASE,
  genericName TEXT COLLATE NOCASE,
  dosageFormName TEXT COLLATE NOCASE,
  strength TEXT,
  packSize TEXT,
  companyName TEXT COLLATE NOCASE,
  countryOfOrigin TEXT COLLATE NOCASE,
  agentName TEXT COLLATE NOCASE
) WITHOUT ROWID;

-- Index every column to speed up queries
CREATE INDEX idx_no ON mytable(no);
CREATE INDEX idx_brandName ON mytable(brandName);
CREATE INDEX idx_genericName ON mytable(genericName);
CREATE INDEX idx_dosageFormName ON mytable(dosageFormName);
CREATE INDEX idx_strength ON mytable(strength);
CREATE INDEX idx_packSize ON mytable(packSize);
CREATE INDEX idx_companyName ON mytable(companyName);
CREATE INDEX idx_countryOfOrigin ON mytable(countryOfOrigin);
CREATE INDEX idx_agentName ON mytable(agentName);

